import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router'; //para recibir valores de otro componente
import { MovieServiceService } from 'src/app/services/movie-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent {

  constructor(private serviceMovie: MovieServiceService, private routeAct: ActivatedRoute, private router: Router) {}

  idPelicula: string;
  pelicula: any


  ngOnInit(): void {
    this.idPelicula = this.routeAct.snapshot.params["id"]
    this.serviceMovie.movie(this.idPelicula).subscribe(
      (pelicula => {
        this.pelicula = pelicula
        console.log(pelicula );
      }),
      (error => {
        console.log("err: " + error);
      })
    )
  }

}
<div class="contenedor ">

    <div class="tarjeta card p-5 shadow w-75 h-auto">

        <img class="w50" [src]="pelicula?.Poster" [alt]="pelicula?.Title">

    <div class="cont2">
        <h1> {{pelicula?.Title}}</h1>
        <p class="p-5"><span>Año:</span> {{pelicula?.Year}}</p>
        <p><span>Pais:</span> {{pelicula?.Country}}</p>
        <p><span>Fecha Estreno:</span> {{pelicula?.DVD}}</p>
        <p><span>Genero:</span> {{pelicula?.Genre}}</p>
        <p><span>Tipo:</span> {{pelicula?.Type}}</p>
        <p><span>Total de Votos:</span> {{pelicula?.imdbVotes}}</p>
    </div>
</div>
</div>
