import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http' //comunicación http y acceder a métodos http
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class MovieServiceService {

  private API_URL: string = 'https://omdbapi.com/?apikey=e9e40461';

  constructor(private http: HttpClient) { }

  getMovies(nombre: string): Observable<any> {
    return this.http.get(`${this.API_URL}&s=${nombre}`)
  }


}
