import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router'; //para recibir valores de otro componente
import {movi}

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent {

  constructor(private serviceMovie: MovieServiceService, private routeAct: ActivatedRoute) {}

  idPelicula: string;
  pelicula: any


  ngOnInit(): void {
    this.idPelicula = this.routerAct.snapshot.params["id"]
    this.servicio.verPeli(this.idPelicula).subscribe(
      (pelicula => {
        this.pelicula = pelicula
        console.log(pelicula );

      }),
      (error => {
        console.log(error);
      })
    )
  }


}
