import { Component, OnInit } from '@angular/core';
import { MovieServiceService } from 'src/app/services/movie-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-search-movie',
  templateUrl: './search-movie.component.html',
  styleUrls: ['./search-movie.component.css']
})
export class SearchMovieComponent {

  constructor(private serviceMovie: MovieServiceService, private router:) {}

  peliculaBuscar: string = "";
  peliculas: any[] = []


  getMovies() {
    console.log(this.peliculaBuscar);

    this.serviceMovie.getMovies(this.peliculaBuscar).subscribe
      (respuesta => {
        console.log(respuesta);
        this.peliculas = respuesta.Search        
      }),
      (error => {
        console.log(error);
      })
  }

  movie(id:string) {

  }

  onKeyPress(event: KeyboardEvent) {
    if (event.keyCode === 13) {
      this.getMovies()
    }
  }
}
